import React from 'react'

class Restaurant_details extends React.Component {
    constructor(props){
        super(props);
        this.state={
            error:null,
            isLoaded:false,
            list:[]
        }
    }
    componentDidMount(){
        fetch("https://resto.com.my/api/frontEnd/restaurant/restaurantsDetails")
        .then(res=>res.json())
        .then(
            (result)=>{
                this.setState({
                    isLoaded: true,
                    list: result.Data
                })
            },

            (error)=>{
                this.setState({
                    isLoaded: true,
                    error
                })
            }
        )
    }

    render() {
        const { error, isLoaded, list } = this.state;
        if (error) {
            return <div>Error: {error.message}</div>;
          } else if (!isLoaded) {
            return <div>Loading...</div>;
          } else {
          
        return (
            <div>
                 
        <div className="container">
            
             <div className="category">
                <div className="categorybrd" >
                <b>134 restaurants</b>
                </div>
                <div className="sub-category">
                <a className="filters" href="">Relavence</a>
                <a className="filters" href="">Cost For Two</a>
                <a className="filters" href="">Delivery Time</a>
                <a className="filters" href="">Rating</a>
                </div>
            </div>
			{list.map(listitem =>(

            	
            <div className="col-xs-12 col-sm-7 col-md-7 col-lg-3" key={listitem.branch_id}>
                    
                    <div className="bg-gray restaurant-entry">
                        <div className="row">
                            <div className="imgcvr">
                                <div className="entry-logo">
                                    <a className="img-fluid" href="Restaurent_details.html">
                                        <img src="dist/images/logo/food15.jpg" alt="Food logo"/>
                                    </a>
                                </div>
                               
                                <div className="entry-dscr"> 
                                            <h5>
                                                Tamilan Mess
                                    </h5>
                                   <span>North Indian, Chinese, Snacks</span>
                                    <ul className="list-inline">
                                        <li className="list-inline-item">
                                        
                                        <span className="star"> <i className="fa fa-star startimg" aria-hidden="true"></i>
                                          4.5</span></li>
                                        <span className="locmark">Thambaram</span>
                                        <span className="offer"><img src="dist/images/off.png" width="30" height="30"/>
                                        10 % Off on all items
                                        </span>
                                    </ul>
                                  
                                </div>
                               
                            </div>
                           
                        </div>
                      
                    </div>
                  

                   

    </div>
            ))}			
            </div>
        </div>
        )
          }
    }
}

export default Restaurant_details
